package org.app.enjoy.music.interfaces;import org.app.enjoy.music.data.MusicData;import java.util.List;/** * Created by victor on 2016/6/13. */public interface DbInterface {    /**     * @param category     * 增加列表分类     */    void addCategory(String category);    /**     * @param category     * 删除列表分类     */    void removeCategory(String category);    /**     * @return     * 查询所有列表分类     */    List<String> queryCategory();    /**     * @return     * 根据category查询列表分类     */    List<String> queryCategory(String category);    /**     * @param tbName     * 清空表数据     */    void clearTb(String tbName);    /**     * @param musicData     * 添加音乐     */    void addMusic(MusicData musicData, String categoryType);    /**     * @param id     * 删除音乐     */    void removeMusic(int id);    /**     * @param category     * 根据类别删除音乐     */    void removeMusicByCategory(String category);    /**     * @param category     * @return     * 根据列表分类查询音乐     */    List<MusicData> queryMusicByCategory(String category);    /**     * @param artist     * @return     * 根据艺术家查询音乐     */    List<MusicData> queryMusicByArtist(String artist);    /**     * @param albumId     * @return     * 根据专辑查询音乐     */    List<MusicData> queryMusicByAlbum(String albumId);    /**     * @param category     * @param id     * 根据列表分类和音乐id查询     * @return     */    List<MusicData> queryMusicByCategoryAndId (String category,int id);}